const BLOG_ENTRY_REGISTRY = new Map<Type<unknown>, BlogEntryConfig>();

export function BlogEntry(config: BlogEntryConfig) {
  return function <T extends Type<unknown>>(target: T): T {
    BLOG_ENTRY_REGISTRY.set(target, config);
    return target;
  };
}

